import React, { useEffect, useState, useRef } from 'react';
import html2canvas from 'html2canvas';
import { uploadImages } from '../services/api.js';

const ImageCapture = ({ isGameActive }) => {
    const [screenshot, setScreenshot] = useState(null);
    const [webcamImage, setWebcamImage] = useState(null);
    const videoRef = useRef(null);
    const capturingRef = useRef(false);
    const [sessionId, setSessionId] = useState(null);

    const captureScreenshot = async () => {
        const canvas = await html2canvas(document.body);
        canvas.toBlob(blob => {
            const file = new File([blob], 'screenshot.png', { type: 'image/png' });
            setScreenshot(file);
        }, 'image/png');
    };

    const captureWebcamImage = () => {
        if (videoRef.current) {
            const canvas = document.createElement('canvas');
            canvas.width = videoRef.current.videoWidth;
            canvas.height = videoRef.current.videoHeight;
            const ctx = canvas.getContext('2d');
            ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
            canvas.toBlob(blob => {
                const file = new File([blob], 'webcam.png', { type: 'image/png' });
                setWebcamImage(file);
            }, 'image/png');
        }
    };

    const captureImages = () => {
        if (!capturingRef.current) {
            capturingRef.current = true;
            captureScreenshot();
            captureWebcamImage();
            setTimeout(() => {
                capturingRef.current = false;
            }, 1000);
        }
    };

    useEffect(() => {
        const upload = async () => {
            if (screenshot && webcamImage && sessionId) {
                try {
                    await uploadImages(screenshot, webcamImage, sessionId);
                    console.log('Images uploaded successfully');
                    setScreenshot(null);
                    setWebcamImage(null);
                } catch (error) {
                    console.error('Error uploading images:', error);
                }
            }
        };
        upload();
    }, [screenshot, webcamImage, sessionId]);

    useEffect(() => {
        const startWebcam = async () => {
            try {
                const stream = await navigator.mediaDevices.getUserMedia({ video: true });
                if (videoRef.current) {
                    videoRef.current.srcObject = stream;
                }
                const response = await fetch('http://localhost:5000/start-session');
                const data = await response.json();
                setSessionId(data.sessionId);
            } catch (error) {
                console.error('Error accessing webcam or creating session:', error);
            }
        };

        if (isGameActive) {
            startWebcam();
            const intervalId = setInterval(captureImages, 5000);
            return () => clearInterval(intervalId);
        }
    }, [isGameActive]);

    return <video ref={videoRef} autoPlay style={{ display: 'none' }} />;
};

export default ImageCapture;
